package org.cuatrovientos.loops;

import java.util.Scanner;

/**
 * @author PC
 *
 */
public class LoopsExercise8 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Table: ");
		for (int i = 0; i < 10; i++) {
				for (int j = 0; j < 10; j++) {
				System.out.print(j);
			}
				System.out.println("x" + i);
			}

	}

}
